// src/components/GetFlightById.tsx
import React, { useState } from 'react';
import { getFlightById } from '../api';

const GetFlightById: React.FC = () => {
  const [id, setId] = useState<number | null>(null);
  const [flight, setFlight] = useState<any>(null);

  const handleFetch = async () => {
    if (id !== null) {
      try {
        const response = await getFlightById(id);
        setFlight(response.data);
      } catch (error) {
        console.error(error);
      }
    }
  };

  return (
    <div>
      <h2>Get Flight by ID</h2>
      <input
        type="number"
        value={id ?? ''}
        onChange={(e) => setId(Number(e.target.value))}
        placeholder="Enter flight ID"
      />
      <button onClick={handleFetch}>Fetch Flight</button>
      {flight && (
        <div>
          <p>Flight ID: {flight.id}</p>
          <p>Origin: {flight.origin}</p>
          <p>Destination: {flight.destination}</p>
          <p>Departure Date: {flight.departureDate}</p>
          <p>Return Date: {flight.returnDate}</p>
          <p>Status: {flight.status}</p>
          <p>Price: ${flight.price}</p>
          <p>Available Seats: {flight.availableSeats}</p>
        </div>
      )}
    </div>
  );
};

export default GetFlightById;
