// src/components/BookFlight.tsx
import React, { useState, useEffect } from 'react';
import { getAllFlights, createBooking, getFlightById } from '../api';
import { useParams } from 'react-router-dom';
import './BookFlight.css';

const BookFlight: React.FC = () => {
  const [flights, setFlights] = useState<any[]>([]);
  const [selectedFlight, setSelectedFlight] = useState<number | null>(null);
  const [flightDetails, setFlightDetails] = useState<any>(null);
  const [passengerName, setPassengerName] = useState('');
  const { flightId } = useParams<{ flightId: string }>();

  useEffect(() => {
    const fetchFlights = async () => {
      try {
        const response = await getAllFlights();
        setFlights(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    const fetchFlightDetails = async () => {
      if (flightId) {
        try {
          const response = await getFlightById(Number(flightId));
          setFlightDetails(response.data);
          setSelectedFlight(response.data.id);
        } catch (error) {
          console.error(error);
        }
      }
    };

    fetchFlights();
    fetchFlightDetails();
  }, [flightId]);

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    const userId = Number(localStorage.getItem('userId')); // Assuming you store userId in localStorage after login
    const seatNumber = `A${Math.floor(Math.random() * 100)}`; // Random seat number for simplicity

    if (selectedFlight && passengerName && userId) {
      try {
        await createBooking({ flightId: selectedFlight, userId, passengerName, seatNumber });
        alert('Flight booked successfully');
      } catch (error) {
        console.error(error);
        alert('Error booking flight');
      }
    }
  };

  return (
    <div className="book-flight-container">
      <h2>Book Flight</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Flight:</label>
          <select onChange={(e) => setSelectedFlight(Number(e.target.value))} value={selectedFlight || ''}>
            <option value="">Select a flight</option>
            {flights.map((flight: any) => (
              <option key={flight.id} value={flight.id}>{flight.destination} - ${flight.price}</option>
            ))}
          </select>
          {flightDetails && (
            <div>
              <p>Origin: {flightDetails.origin}</p>
              <p>Destination: {flightDetails.destination}</p>
              <p>Departure Date: {flightDetails.departureDate}</p>
              <p>Price: ${flightDetails.price}</p>
            </div>
          )}
        </div>
        <div>
          <label>Passenger Name:</label>
          <input type="text" value={passengerName} onChange={(e) => setPassengerName(e.target.value)} />
        </div>
        <button type="submit">Book Flight</button>
      </form>
    </div>
  );
};

export default BookFlight;
