// src/components/AddFlight.tsx
import React, { useState } from 'react';
import { createFlight } from '../api';

const AddFlight: React.FC = () => {
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [status, setStatus] = useState('AVAILABLE');
  const [price, setPrice] = useState(0);
  const [availableSeats, setAvailableSeats] = useState(0);
  const [message, setMessage] = useState('');

  const handleAddFlight = async (event: React.FormEvent) => {
    event.preventDefault();
    try {
      await createFlight({
        origin,
        destination,
        departureDate,
        returnDate,
        status,
        price,
        availableSeats,
      });
      setMessage('Flight added successfully!');
      // Clear form fields
      setOrigin('');
      setDestination('');
      setDepartureDate('');
      setReturnDate('');
      setStatus('AVAILABLE');
      setPrice(0);
      setAvailableSeats(0);
    } catch (error) {
      setMessage('Error adding flight. Please try again.');
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Add Flight</h2>
      <form onSubmit={handleAddFlight}>
        <div>
          <label>Origin:</label>
          <input
            type="text"
            value={origin}
            onChange={(e) => setOrigin(e.target.value)}
          />
        </div>
        <div>
          <label>Destination:</label>
          <input
            type="text"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
          />
        </div>
        <div>
          <label>Departure Date:</label>
          <input
            type="date"
            value={departureDate}
            onChange={(e) => setDepartureDate(e.target.value)}
          />
        </div>
        <div>
          <label>Return Date:</label>
          <input
            type="date"
            value={returnDate}
            onChange={(e) => setReturnDate(e.target.value)}
          />
        </div>
        <div>
          <label>Status:</label>
          <select value={status} onChange={(e) => setStatus(e.target.value)}>
            <option value="AVAILABLE">AVAILABLE</option>
            <option value="BOOKED">BOOKED</option>
          </select>
        </div>
        <div>
          <label>Price:</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(Number(e.target.value))}
          />
        </div>
        <div>
          <label>Available Seats:</label>
          <input
            type="number"
            value={availableSeats}
            onChange={(e) => setAvailableSeats(Number(e.target.value))}
          />
        </div>
        <button type="submit">Add Flight</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AddFlight;
