// src/components/GetBookingById.tsx
import React, { useState } from 'react';
import { getBookingById } from '../api';

const GetBookingById: React.FC = () => {
  const [id, setId] = useState<number | null>(null);
  const [booking, setBooking] = useState<any>(null);

  const handleFetch = async () => {
    if (id !== null) {
      try {
        const response = await getBookingById(id);
        setBooking(response.data);
      } catch (error) {
        console.error(error);
      }
    }
  };

  return (
    <div>
      <h2>Get Booking by ID</h2>
      <input
        type="number"
        value={id ?? ''}
        onChange={(e) => setId(Number(e.target.value))}
        placeholder="Enter booking ID"
      />
      <button onClick={handleFetch}>Fetch Booking</button>
      {booking && (
        <div>
          <p>Booking ID: {booking.id}</p>
          <p>Flight ID: {booking.flightId}</p>
          <p>User ID: {booking.userId}</p>
          <p>Passenger Name: {booking.passengerName}</p>
          <p>Seat Number: {booking.seatNumber}</p>
        </div>
      )}
    </div>
  );
};

export default GetBookingById;
