// src/api.ts

import axios from 'axios';

const API_URL = 'http://localhost:8080/api';

// Create a flight
export const createFlight = (flight: any) => axios.post(`${API_URL}/flights`, flight);

// Get all flights
export const getAllFlights = () => axios.get(`${API_URL}/flights`);

// Get a flight by ID
export const getFlightById = (id: number) => axios.get(`${API_URL}/flights/${id}`);

// Search flights by destination
export const searchFlights = (destination: string) => axios.get(`${API_URL}/flights/search?destination=${destination}`);

// Create a booking
export const createBooking = (booking: any) => axios.post(`${API_URL}/bookings`, booking);

// Get all bookings
export const getAllBookings = () => axios.get(`${API_URL}/bookings`);

// Get a booking by ID
export const getBookingById = (id: number) => axios.get(`${API_URL}/bookings/${id}`);

// Register a new user
export const registerUser = (user: any) => axios.post(`${API_URL}/users/register`, user);

// Get all users
export const getAllUsers = () => axios.get(`${API_URL}/users`);

// Get a user by ID
export const getUserById = (id: number) => axios.get(`${API_URL}/users/${id}`);
