// src/components/BookFlight.tsx
import React, { useState, useEffect } from 'react';
import { getAllFlights, createBooking } from '../api';

const BookFlight: React.FC = () => {
  const [flights, setFlights] = useState<any[]>([]);
  const [selectedFlight, setSelectedFlight] = useState<number | null>(null);
  const [passengerName, setPassengerName] = useState('');
  const [seatNumber, setSeatNumber] = useState('');
  const [userId, setUserId] = useState<number | null>(null);

  useEffect(() => {
    const fetchFlights = async () => {
      try {
        const response = await getAllFlights();
        setFlights(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchFlights();
  }, []);

  useEffect(() => {
    // Fetch user ID from local storage or context if available
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setUserId(Number(storedUserId));
    }
  }, []);

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    if (selectedFlight && passengerName && seatNumber && userId !== null) {
      try {
        await createBooking({ flightId: selectedFlight, userId, passengerName, seatNumber });
        alert('Flight booked successfully');
      } catch (error) {
        console.error(error);
        alert('Error booking flight');
      }
    }
  };

  return (
    <div>
      <h2>Book Flight</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Flight:</label>
          <select onChange={(e) => setSelectedFlight(Number(e.target.value))}>
            <option value="">Select a flight</option>
            {flights.map((flight: any) => (
              <option key={flight.id} value={flight.id}>
                {flight.origin} to {flight.destination} - {flight.departureDate} - ${flight.price}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Passenger Name:</label>
          <input type="text" value={passengerName} onChange={(e) => setPassengerName(e.target.value)} />
        </div>
        <div>
          <label>Seat Number:</label>
          <input type="text" value={seatNumber} onChange={(e) => setSeatNumber(e.target.value)} />
        </div>
        <button type="submit">Book Flight</button>
      </form>
    </div>
  );
};

export default BookFlight;
