// src/api.ts
import axios from 'axios';

// Create an Axios instance with a base URL
const api = axios.create({
  baseURL: 'http://localhost:8080/api', // Replace with your backend base URL
  headers: {
    'Content-Type': 'application/json',
  },
});

// User-related endpoints
export const registerUser = (data: { name: string, email: string, password: string }) => 
  api.post('/users/register', data);

// Booking-related endpoints
export const createBooking = (data: { flightId: number, passengerName: string }) => 
  api.post('/bookings', data);

export const getAllBookings = () => 
  api.get('/bookings');

export const getBookingById = (id: number) => 
  api.get(`/bookings/${id}`);

// Flight-related endpoints
export const getAllFlights = () => 
  api.get('/flights');

export const getFlightById = (id: number) => 
  api.get(`/flights/${id}`);

export const searchFlights = (destination: string) => 
  api.get('/flights/search', { params: { destination } });

export default api;
